<script>
import {
  startApi,
  getAllCertificates,
  preLoadKey,
  postLoadKey,
} from "@/services/eimzo/index";
import Notification from "../components/Notification.vue";
import NotificationModal from "../components/NotificationModal.vue";
import ActModal from "../components/ActModal.vue";
import Vqr from "v-qr";
export default {
  components: { Notification, NotificationModal, ActModal, Vqr },
  data() {
    return {
      EspModal: false,
      keys: null,
      result: null,
      selectedKey: null,
      timeOut: null,
      message: [],
      messageAct: [],
      userData: null,
      qrUrl: "",
      logo: "",
    };
  },
  created() {
    this.qrUrl = `https://zerox.uz/user`;
    this.logo =
      "data:image/png;base64,";

    // this.$store.commit("changeLoading", true);
    this.$nuxt.$on("forceUpdateParent", ($event) => {
      // this.getMyInfo();
      this.getNotifications();
    });
  },
  computed: {
    isOpen() {
      return this.$store.state.isOpen;
    },
    isModalInfo() {
      return this.$store.getters.isModalInfo;
    },
  },
  async mounted() {
    this.qrUrl = `https://zerox.uz/user`;
    this.logo =
      "data:image/png;base64,";

    if (this.$auth.loggedIn) {
      // await this.getMyInfo();
      await this.getNotifications();
      //  try {
      //    this.socket = this.$nuxtSocket({
      //      channel: "/",
      //      name:'home',
      //      secure: true,
      //    });
      //  } catch (err) {
      //   console.log(err);
      // }


    }
    // this.$store.commit("changeLoading", false);
  },
  methods: {
    isModalActive() {
      this.$store.dispatch("IsActiveModal");
    },
    closeMenu() {
      this.$store.commit("Media_Menu_Close", false);
    },
    async getMyInfo() {
      try {
        const response = await fetch("https://ipapi.co/json/");
        const data = await response.json();
        if (data) {
          this.userData = data;
        }
      } catch (e) {
        console.log(e);
      }
    },

    async getNotifications() {
      try {
        const response = await this.$axios.get("/notification/me");

        if (response.status == 200) {
          this.message = response.data.data;
        }
      } catch (e) {
        console.log(e);
      }
    },

    async reject(id) {
      try {
        const response = await this.$axios.put(`/notification/success/${id}`, {
          status: 2,
        });
        if (response.status == 200) {
          this.$toast.success("Shartnoma bekor qilindi");
          this.getNotifications();
        }
      } catch (e) {
        console.log(e);
      }
    },

    async affirm(id) {
      try {
        const response = await this.$axios.put(`/notification/success/${id}`, {
          status: 1,
        });
        if (response.status == 200) {
          this.$toast.success("Shartnoma tasdiqlandi");
          this.getNotifications();
        }
      } catch (e) {
        console.log(e);
      }
    },
  },
};
</script>
